function modal_form(){const e=document.querySelectorAll('[data-modal="consultation"]'),o=document.querySelector(".overlay"),t=document.querySelector("#consultation"),l=document.querySelector(".modal__close");e.forEach((e=>{e.addEventListener("click",(()=>{o.classList.toggle("hide"),t.classList.toggle("hide"),document.body.style.overflow="hidden"}))})),l.addEventListener("click",(()=>{o.classList.toggle("hide"),t.classList.toggle("hide"),document.body.style.overflow=""}))}export default modal_form;